mapper.select:
	select a.*,b.HOMENAME,b.HEATINGAREA,b.HEATINGSTATUS,c.HEATINGDAY,d.HOODNAME from HT_HOMEFEE a 
  	inner join HT_HOME b on a.HOME=b.HOMENO
  	inner join HT_HEATINGPRICE c on a.HEATINGYEAR=c.HEATINGYEAR
  	inner join 
  	(select home.HOMENO,neighbourhood.HOODNAME 
  	 from HT_HOME home
  	 inner join HT_NEIGHBOURHOOD neighbourhood on home.HOODNO=neighbourhood.HOODNO ) d on on a.HOME=d.HOMENO
  
     
      	and a.HOODNO=#{hoodno}
     
     
     	and a.HEATINGYEAR=#{heatingyear}
      
     
     	and a.FEESTATUS=#{feestatus}
      
  
    limit #{start},#{rows}
